from bs4 import BeautifulSoup
import re
import requests

def create_connect():
    import pymysql
    try:
        connect = pymysql.connect('localhost','root','1987',db='test',charset = 'utf8')
        cursor = connect.cursor()
        return connect,cursor
    except Exception as e :
        print (e)
    

def execute_sql(connect,cursor,sqls):
    sql = sqls.split(';')
    try:
        for item in sql :
            
            cursor.execute(item)
            connect.commit()

    except Exception as e :
        print (e)
        connect.rollback()


def close_connect(connect,cursor):
    try:
        cursor.close()
        connect.close()
    except Exception as e :
        print (e)


connect,cursor = create_connect()


